import React, { useEffect, useState } from 'react';
import { verifyOtpAndGetToken, fetchRazorpayKey, createOrder, initiatePayment, verifyOrder } from '../api';
import './Payment.css'; // Optional: for styling

const Payment = () => {
  const [token, setToken] = useState('');
  const [razorpayKey, setRazorpayKey] = useState('rzp_test_qUtLOVt2Ge3dFM');

  useEffect(() => {
    const initializePayment = async () => {
      try {
        // Generate the token
        const generatedToken = await verifyOtpAndGetToken();
        setToken(generatedToken);

        // Fetch the Razorpay key
        const key = await fetchRazorpayKey(generatedToken);
        setRazorpayKey(key);
      } catch (error) {
        console.error('Initialization error:', error);
      }
    };

    // Ensure Razorpay script is loaded
    const checkRazorpayScript = () => {
      if (!window.Razorpay) {
        const script = document.createElement('script');
        script.src = 'https://checkout.razorpay.com/v1/checkout.js';
        script.onload = () => {
          initializePayment();
        };
        script.onerror = () => {
          console.error('Failed to load Razorpay script');
        };
        document.body.appendChild(script);
      } else {
        initializePayment();
      }
    };

    checkRazorpayScript();
  }, []);

  const handlePayment = async () => {
    try {
      // Create an order
      const transactionId = await createOrder(token, '6613d6fbbf1afca9aa1b519e', '662caa2d50bf43b5cef75232', 441, 'NEET25');
      // Initiate payment
      await initiatePayment(transactionId, razorpayKey, (paymentId, signature) =>
        verifyOrder(token, paymentId, signature, transactionId)
      );
    } catch (error) {
      console.error('Payment error:', error);
    }
  };

  return (
    <div className="payment-container">
      <button onClick={handlePayment} disabled={!razorpayKey}>
        Pay Now
      </button>
    </div>
  );
};

export default Payment;
